// TaskList.tsx
import React, { useEffect } from 'react';
import Task from './Task.ts';


interface TaskListProps {
    tasks: Task[];
    onDelete: (id: number) => void;
    onToggle: (id: number) => void;
}

const TaskList: React.FC<TaskListProps> = ({ tasks, onDelete, onToggle }) => {


    useEffect(() => {
        fetch('http://localhost:3001/Alltasks')
            .then(response => response.json())
            .then(response => {
                for (let i = 0; i < response.length; i++) {
                    <li key={response.id}>
                        <input
                            type="checkbox"
                            checked={response.completed}
                            onChange={() => onToggle(response.id)}
                        />
                        <span style={{ textDecoration: response.completed ? 'line-through' : 'none' }}>
                            {response.title}
                        </span>
                        <button onClick={() => onDelete(response.id)}>Delete</button>
                    </li>
                }
            });
    }, []);
    return (
        <ul>
            {tasks.map(task => (
                <li key={task.id}>
                    <input
                        type="checkbox"
                        checked={task.completed}
                        onChange={() => onToggle(task.id)}
                    />
                    <span style={{ textDecoration: task.completed ? 'line-through' : 'none' }}>
                        {task.title}
                    </span>
                    <button onClick={() => onDelete(task.id)}>Delete</button>
                </li>
            ))}
        </ul>
    );
};

export default TaskList;
